mapper.update:
    	UPDATE cart
		   SET cart_count = #{cartCount},
			   cart_price = #{cartPrice},
			   modify_dt = SYSDATE,
			   modifier = #{customerId}
		 WHERE cart_id = #{cartId}

    	UPDATE cart
		   SET status = 'N',
		       modify_dt = SYSDATE,
		       modifier = #{customerId}
		 WHERE cart_id IN

				 	#{cartId}

    	UPDATE cart
		   SET checked = #{checked},
		       modify_dt = SYSDATE,
		       modifier = #{customerId}
		 WHERE cart_id IN 

				 	#{cartId}
