mapper.insert:
DECLARE shipping_dv_num NUMBER; BEGIN INSERT INTO shipping ( shipping_id, customer_id, shipping_receiver_nm, shipping_receiver_telno, shipping_dmnd, shipping_road_nm_addr, shipping_daddr, shipping_dv, creator ) VALUES ( shipping_seq.nextval, #{customerId}, #{shippingAdd.shippingReceiverNm}, #{shippingAdd.shippingReceiverTelno}, #{shippingAdd.shippingDmnd}, #{shippingAdd.shippingRoadNmAddr}, #{shippingAdd.shippingDaddr}, #{shippingAdd.shippingDv}, #{customerId} );
SELECT shipping_dv INTO shipping_dv_num FROM shipping WHERE customer_id = #{customerId} AND shipping_id = ( SELECT shipping_id FROM shipping WHERE customer_id = #{customerId} AND create_dt = ( SELECT MAX(create_dt) FROM shipping WHERE customer_id = #{customerId} ) );
IF shipping_dv_num = 301 THEN UPDATE shipping SET shipping_dv = 302, modifier = #{customerId}, modify_dt = SYSTIMESTAMP WHERE shipping_dv = 301 AND customer_id = #{customerId} AND shipping_id != ( SELECT shipping_id FROM shipping WHERE customer_id = #{customerId} AND create_dt = ( SELECT MAX(create_dt) FROM shipping WHERE customer_id = #{customerId} ) ); END IF; END;

mapper.update:
DECLARE shipping_dv_num NUMBER; BEGIN UPDATE shipping SET shipping_receiver_nm = #{shippingUpdate.shippingReceiverNm}, shipping_receiver_telno = #{shippingUpdate.shippingReceiverTelno}, shipping_dmnd = #{shippingUpdate.shippingDmnd}, shipping_road_nm_addr = #{shippingUpdate.shippingRoadNmAddr}, shipping_daddr = #{shippingUpdate.shippingDaddr}, shipping_dv = TO_NUMBER(#{shippingUpdate.shippingDv}), modifier = #{customerId}, modify_dt = SYSTIMESTAMP WHERE shipping_id = #{shippingId};
SELECT shipping_dv INTO shipping_dv_num FROM shipping WHERE shipping_id = #{shippingId};
IF shipping_dv_num = 301 THEN UPDATE shipping SET shipping_dv = 302, modifier = #{customerId}, modify_dt = SYSTIMESTAMP WHERE customer_id = #{customerId} AND shipping_dv = 301 AND shipping_id != #{shippingId}; END IF; END;

mapper.delete:
 	UPDATE shipping
	   SET status='N'
	WHERE shipping_id=#{shippingId}

mapper.update:
	 	UPDATE  shipping
       	   SET  shipping_dv = #{shippingDv}
		 WHERE  shipping_id=#{shippingId}
		   AND  status = 'Y'
